/***************************************************************************************************************/package employee; import java.util.concurrent.TimeUnit;import java.util.ArrayList; public class PayRoll  { public ArrayList<employ> l; public PayRoll() {l = new ArrayList<>();}public void add(employ f) {l.add(f);}public double[] pay() {double pays[] = new double[l.size()];int i = 0;for (employ employee : l) {pays[i] = (double) employee.getPay();i++;System.out.println("I'm going to sleep");try {TimeUnit.SECONDS.sleep(1);} catch (InterruptedException e) {e.printStackTrace();}}return pays;}}
